import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const CategoryButtons = ({ content, rightActiveTab, selectedCategory, setSelectedCategory }) => {
  const categories = content[rightActiveTab]?.categories || [];
  const [isInitialLoad, setIsInitialLoad] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsInitialLoad(true);
    }, 5000); // 5-second delay for the first load

    return () => clearTimeout(timer);
  }, []);

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={rightActiveTab}
        className="category-buttons"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }} // Define fade-out effect
        transition={{
          duration: isInitialLoad ? .5 : 1.5, // Keep existing durations
          delay: isInitialLoad ? .5 : 0, // Adjust delay only for initial load
        }}
      >
        {categories.map((category, index) => (
          <button
            key={index}
            onClick={() => setSelectedCategory(index)}
            className={selectedCategory === index ? 'active' : ''}
          >
            {category.name}
          </button>
        ))}
      </motion.div>
    </AnimatePresence>
  );
};

export default CategoryButtons;
