import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion'; // Import framer-motion
import '../../styles/main.css';
import data from '../../data/illustrations.json';
import illustrationMap from '../../data/Illustrationmap';
import ContentBox from './Scrolls/ContentBox';
import SlideContent from './Scrolls/SlideContent';
import CategoryButtons from './Scrolls/CategoryButtons';
import ScrollIndicator from './Scrolls/ScrollIndicator';
import DotIndicator from './Scrolls/DotIndicator';

const RightContent = ({ rightActiveTab }) => {
  const [slideIndex, setSlideIndex] = useState({
    appDesign: 0,
    webDesign: 0,
    bookCover: 0,
    illustrations: 0,
  });
  const [selectedCategory, setSelectedCategory] = useState(0);

  const content = data;

  useEffect(() => {
    setSlideIndex((prev) => ({
      ...prev,
      [rightActiveTab]: 0,
    }));
  }, [rightActiveTab, selectedCategory]);

  useEffect(() => {
    const handleWheel = (event) => {
      event.preventDefault();
      const currentTabIndex = slideIndex[rightActiveTab];

      const currentTabContent = content[rightActiveTab];
      if (currentTabContent?.categories?.[selectedCategory]?.images) {
        setSlideIndex((prev) => ({
          ...prev,
          [rightActiveTab]: event.deltaY > 0
            ? Math.min(currentTabContent.categories[selectedCategory].images.length - 1, currentTabIndex + 1)
            : Math.max(0, currentTabIndex - 1),
        }));
      } else if (currentTabContent) {
        setSlideIndex((prev) => ({
          ...prev,
          [rightActiveTab]: event.deltaY > 0
            ? Math.min(currentTabContent?.categories?.[selectedCategory]?.images?.length - 1 || 0, currentTabIndex + 1)
            : Math.max(0, currentTabIndex - 1),
        }));
      }
    };

    window.addEventListener('wheel', handleWheel, { passive: false });
    return () => {
      window.removeEventListener('wheel', handleWheel);
    };
  }, [rightActiveTab, selectedCategory, slideIndex]);

  const getSlideImage = (imagePath) => {
    return illustrationMap[imagePath] || imagePath;
  };

  const getContentBoxStyle = () => ({
    maxHeight: '80vh',
  });

  const getSlideContent = () => {
    const currentTabContent = content[rightActiveTab];
    if (currentTabContent?.categories) {
      return {
        image: getSlideImage(currentTabContent.categories[selectedCategory]?.images[slideIndex[rightActiveTab]]),
      };
    }
    return null;
  };

  return (
    <div className="right-content">
      <div className="slider-container">
        <ContentBox style={getContentBoxStyle()}>
          <motion.div
            key={slideIndex[rightActiveTab]} // Trigger animation on slide index change
            initial={{ opacity: 0 }}  // Initial opacity when content appears
            animate={{ opacity: 1 }}  // Final opacity when content is visible
            exit={{ opacity: 0 }}     // Fade out when content is removed
            transition={{ duration: 1 }} // Duration for fade transition
          >
            <SlideContent slide={getSlideContent()} />
          </motion.div>
        </ContentBox>

        {content[rightActiveTab]?.categories?.length > 0 && (
          <CategoryButtons
            content={content}
            rightActiveTab={rightActiveTab}
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        )}

        <DotIndicator
          activeSlide={slideIndex[rightActiveTab]}
          totalSlides={content[rightActiveTab]?.categories?.[selectedCategory]?.images?.length || 0}
        />

        <ScrollIndicator />
      </div>
    </div>
  );
};

export default RightContent;
