import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Set content visibility to true when it is loaded
    setIsVisible(true);

    // Reset visibility when the content is unloaded
    return () => setIsVisible(false);
  }, [slide]);

  if (!slide || (!slide.image && !slide.text)) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <AnimatePresence>
      <motion.div
        className="slide-content"
        key={slide.image || slide.text} // Ensure the element is identified uniquely by key
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }} // Fade out when leaving
        transition={{ duration: 1 }} // Duration for the fade-in and fade-out effect
      >
        {slide.image && (
          <img
            src={slide.image}
            alt={slide.text || "No description available"}
            className="slide-image"
          />
        )}
        {slide.text && <div className="slide-text">{slide.text}</div>}
      </motion.div>
    </AnimatePresence>
  );
};

export default SlideContent;
