import React from "react";
import "../../../styles/main.css";
import illustrationsData from "../../../data/illustrations.json"; // Import the illustrations data

const SlideContent = ({ slideIndex }) => {
  const slide = illustrationsData.illustrations[slideIndex];

  if (!slide || !slide.image) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <div className="slide-content">
      <img
        src={slide.image}
        alt={slide.text || "No description available"}
        className="slide-image"
      />
      <div className="slide-text">{slide.text}</div>
    </div>
  );
};

export default SlideContent;
