import React from "react";
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  if (!slide || !slide.image) {
    return <div>No image available</div>; // Fallback for missing image
  }

  return (
    <div className="slide-content">
      <img src={slide.image} alt={slide.text} style={{ width: '100%', height: 'auto' }} />
      <p>{slide.text}</p>
    </div>
  );
};

export default SlideContent;
