import React, { useState, useEffect } from "react";
import { CSSTransition } from "react-transition-group";
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
    return () => setIsVisible(false);
  }, [slide]);

  if (!slide) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <CSSTransition
      in={isVisible}
      timeout={1000}
      classNames="fade"
      unmountOnExit
    >
      <div className="slide-content">
        {slide.image && (
          <img src={slide.image} alt="Slide" className="slide-image" />
        )}
        {slide.text && <div className="slide-text">{slide.text}</div>}
      </div>
    </CSSTransition>
  );
};

// Example of usage in parent component
const ParentComponent = () => {
  const [slide, setSlide] = useState({
    image: "path/to/your/image.jpg",
    text: "This is a sample text for the slide",
  });

  return (
    <div>
      <SlideContent slide={slide} />
      {/* Optionally, add a button to change slide */}
      <button onClick={() => setSlide(null)}>Clear Slide</button>
    </div>
  );
};

export default ParentComponent;
