import React, { useState, useEffect, useRef } from "react";
import { gsap } from "gsap";
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(false);
  const contentRef = useRef(null);

  useEffect(() => {
    // Fade-in the content when it is loaded
    setIsVisible(true);

    // Fade-out the content when it is unloaded using GSAP
    return () => {
      gsap.to(contentRef.current, {
        opacity: 0,
        duration: 1,
      });
    };
  }, [slide]);

  if (!slide || (!slide.image && !slide.text)) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <div className="slide-content" ref={contentRef}>
      {slide.image && (
        <img
          src={slide.image}
          alt={slide.text || "No description available"}
          className="slide-image"
        />
      )}
      {slide.text && <div className="slide-text">{slide.text}</div>}
    </div>
  );
};

export default SlideContent;
