import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const CategoryButtons = ({ content, rightActiveTab, selectedCategory, setSelectedCategory }) => {
  // Safe access to categories with fallback
  const categories = content?.[rightActiveTab]?.categories || []; 

  const [isInitialLoad, setIsInitialLoad] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsInitialLoad(false);
    }, 5000); // 5-second delay for the first load

    return () => clearTimeout(timer);
  }, []);

  if (!categories.length) {
    return <div>Loading categories...</div>;  // Handle the case where categories are not loaded
  }

  return (
    <AnimatePresence exitBeforeEnter>
      <motion.div
        key={rightActiveTab}  // Key to ensure unique animation per tab
        className="category-buttons"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: isInitialLoad ? 3.5 : 1.5, delay: isInitialLoad ? 10 : 0 }}
      >
        {categories.map((category, index) => (
          <button
            key={index}
            onClick={() => setSelectedCategory(index)}
            className={selectedCategory === index ? 'active' : ''}
          >
            {category?.name || "Unnamed Category"}  {/* Safe access to name */}
          </button>
        ))}
      </motion.div>
    </AnimatePresence>
  );
};

export default CategoryButtons;
