import React from 'react';
import { motion } from 'framer-motion';
import SlideContent from './SlideContent'; // Assuming you have this component
import CategoryButtons from './CategoryButtons'; // Assuming you have this component

const RightSide = ({ rightActiveTab, selectedCategory, slideIndex, content }) => {
  return (
    <div className="right-side">
      <div className="slider-container">
        {/* Assuming you have a way to display the content */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1.2 }}
        >
          <SlideContent
            fadeKey={0}
            rightActiveTab={rightActiveTab}
            content={content}
            slideIndex={slideIndex}
            selectedCategory={selectedCategory}
          />
        </motion.div>

        {/* Category Buttons */}
        {rightActiveTab === 'illustrations' && content.illustrations?.categories?.length > 0 && (
          <CategoryButtons
            content={content}
            selectedCategory={selectedCategory}
            setSelectedCategory={() => {}}
          />
        )}
      </div>
    </div>
  );
};

export default RightSide;
