import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const CategoryButtons = ({ content, rightActiveTab, selectedCategory, setSelectedCategory }) => {
  const categories = content[rightActiveTab]?.categories || [];
  const [firstLoad, setFirstLoad] = useState(true);

  useEffect(() => {
    // After the first render, set firstLoad to false
    setFirstLoad(false);
  }, []);

  return (
    <AnimatePresence exitBeforeEnter>
      <motion.div
        key={rightActiveTab}
        className="category-buttons"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: firstLoad ? 1 : 2.5 }} // Longer duration for first load
      >
        {categories.map((category, index) => (
          <button
            key={index}
            onClick={() => setSelectedCategory(index)}
            className={selectedCategory === index ? 'active' : ''}
          >
            {category.name}
          </button>
        ))}
      </motion.div>
    </AnimatePresence>
  );
};

export default CategoryButtons;
