import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const ContentBox = ({ children, style }) => {
  const [isFirstLoad, setIsFirstLoad] = useState(true); // State to track the first load

  useEffect(() => {
    // Set isFirstLoad to false after the page has loaded
    const timer = setTimeout(() => setIsFirstLoad(false), 100); // Adjust timeout as needed
    return () => clearTimeout(timer);
  }, []);

  return (
    <motion.div
      className="right-content-box"
      style={style}
      initial={{ opacity: 0 }}
      animate={{
        opacity: isFirstLoad ? 0 : 1, // Only animate on first load
      }}
      transition={{ duration: 2.5, delay: isFirstLoad ? 7 : 0 }} // Set delay based on first load
    >
      {/* Text inside the box with a delay only on first load */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{
          opacity: isFirstLoad ? 0 : 1, // Fade in after refresh
        }}
        transition={{ duration: 2, delay: isFirstLoad ? 2 : 0 }}  // Adjust delay on first load
      >
        {children}
      </motion.div>
    </motion.div>
  );
};

export default ContentBox;
