import React, { useState, useEffect } from "react";
import { CSSTransition } from "react-transition-group";
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Fade-in when content is loaded
    setIsVisible(true);

    // Fade-out when content is unloaded
    return () => setIsVisible(false);
  }, [slide]);

  if (!slide || (!slide.image && !slide.text)) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <CSSTransition
      in={isVisible}
      timeout={1000} // Duration of the fade transition (in ms)
      classNames="fade"
      unmountOnExit
    >
      <div className="slide-content">
        {slide.image && (
          <img
            src={slide.image}
            alt={slide.text || "No description available"}
            className="slide-image"
          />
        )}
        {slide.text && <div className="slide-text">{slide.text}</div>}
      </div>
    </CSSTransition>
  );
};

export default SlideContent;
