import React, { useState, useEffect } from "react";
import "../../styles/main.css";

const RightContent = ({ rightActiveTab }) => {
  const [slideIndex, setSlideIndex] = useState(0);

  const slides = {
    appdesign: [
      { "text": "Designing modern apps with sleek interfaces.", "image": "https://example.com/image1.jpg" },
      { "text": "User-centered design principles are crucial.", "image": "https://example.com/image2.jpg" },
      { "text": "Innovative approaches for app functionality.", "image": "https://example.com/image3.jpg" },
      { "text": "Future of mobile app development.", "image": "https://example.com/image4.jpg" }
    ],
    webdesign: [
      { "text": "Slide 1: Lorem ipsum dolor sit amet, consectetur adipiscing elit.", "image": "https://example.com/webdesign1.jpg" }
    ],
    bookcovers: [
      { "text": "Slide 1: Lorem ipsum dolor sit amet, consectetur adipiscing elit.", "image": "https://example.com/bookcovers1.jpg" }
    ],
    illustrations: [
      { "text": "Slide 1: Lorem ipsum dolor sit amet, consectetur adipiscing elit.", "image": "https://example.com/illustrations1.jpg" }
    ]
  };

  const getContentBoxStyle = () => {
    switch (rightActiveTab) {
      case "appdesign":
        return { backgroundColor: "#ffcccc" }; // Light red
      case "webdesign":
        return { backgroundColor: "#ccffcc" }; // Light green
      case "bookcovers":
        return { backgroundColor: "#ccccff" }; // Light blue
      case "illustrations":
        return { backgroundColor: "#ffffcc" }; // Light yellow
      default:
        return { backgroundColor: "#f0f0f0" }; // Default color
    }
  };

  useEffect(() => {
    setSlideIndex(0); // Reset slide index when changing tabs
  }, [rightActiveTab]);

  const handleNext = () => {
    setSlideIndex((prevIndex) => (prevIndex + 1) % slides[rightActiveTab].length);
  };

  const handlePrev = () => {
    setSlideIndex((prevIndex) => (prevIndex - 1 + slides[rightActiveTab].length) % slides[rightActiveTab].length);
  };

  const renderContent = () => (
    <div className="slider-container">
      <button
        className="arrow left"
        onClick={handlePrev}
        disabled={slideIndex === 0}
      >
        ‹
      </button>
      <div className="right-content-box" style={getContentBoxStyle()}>
        <img src={slides[rightActiveTab][slideIndex].image} alt="Slide" />
        <p>{slides[rightActiveTab][slideIndex].text}</p>
      </div>
      <button
        className="arrow right"
        onClick={handleNext}
        disabled={slideIndex === slides[rightActiveTab].length - 1}
      >
        ›
      </button>
    </div>
  );

  return <div className="right-content">{renderContent()}</div>;
};

export default RightContent;
