import React, { useState, useEffect, useRef } from "react";
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(true);
  const contentRef = useRef(null);

  useEffect(() => {
    // When content is loaded, ensure it's visible
    setIsVisible(true);

    // Reset visibility when content is unloaded
    return () => setIsVisible(false);
  }, [slide]);

  if (!slide || (!slide.image && !slide.text)) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <div
      className={`slide-content ${isVisible ? 'fade-in' : 'fade-out'}`}
      ref={contentRef}
    >
      {slide.image && (
        <img
          src={slide.image}
          alt={slide.text || "No description available"}
          className="slide-image"
        />
      )}
      {slide.text && <div className="slide-text">{slide.text}</div>}
    </div>
  );
};

export default SlideContent;
