import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";  // Import motion for animations
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Update visibility when content is loaded
    setIsVisible(true);

    // Reset visibility when content is unloaded
    return () => setIsVisible(false);
  }, [slide]);

  if (!slide || (!slide.image && !slide.text)) {
    return <div className="slide-content">No content available</div>;
  }

  return (
    <motion.div
      className="slide-content"
      initial={{ opacity: 0, scale: 0.8 }} // Initial state: hidden and scaled down
      animate={{ opacity: isVisible ? 1 : 0, scale: isVisible ? 1 : 0.8 }} // Animate to visible and normal size
      exit={{ opacity: 0, scale: 0.8 }} // Exit state: fade out and scale down
      transition={{ duration: 0.5 }} // Transition duration
    >
      {slide.image && (
        <img
          src={slide.image}
          alt={slide.text || "No description available"}
          className="slide-image"
        />
      )}
      {slide.text && <div className="slide-text">{slide.text}</div>}
    </motion.div>
  );
};

export default SlideContent;
