import React, { useEffect, useRef } from "react";
import { motion, useAnimation } from "framer-motion";

const Footer = ({ activeTab, setActiveTab }) => {
  // Map tab keys to display text
  const tabLabels = {
    about: "About Me",
    resume: "Resume",
    contact: "Contact Me",
  };

  const controls = useAnimation();
  const sliderRef = useRef(null);

  useEffect(() => {
    // Exclude the "Contact Me" tab from the slider animation
    const activeButton = document.querySelector(".tab.active:not(.contact-button)");
    if (activeButton && sliderRef.current) {
      const offsetLeft = activeButton.offsetLeft;
      controls.start({ x: offsetLeft, transition: { duration: 0.025, ease: "easeInOut" } });
    }
  }, [activeTab, controls]);

  return (
    <motion.footer
      className="footer"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 2.5, delay: 2 }}
    >
      <motion.div
        className="footer-slider"
        ref={sliderRef}
        animate={controls}
        style={{ position: "absolute", height: "2px", backgroundColor: "#007acc", width: "100px" }}
      ></motion.div>
      {Object.keys(tabLabels).map((tab) => {
        const isContactTab = tab === "contact";
        return (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? "active" : ""} ${isContactTab ? "contact-button" : ""}`}
            onClick={() => setActiveTab(tab)}
            style={
              isContactTab
                ? {
                    backgroundColor: activeTab === tab ? "#ff2224" : "#ff4447", // Active color
                    color: "#fff",
                    borderRadius: "20px",
                    padding: "10px 20px",
                    transition: "all 0.3s ease-out",
                  }
                : {}
            }
            onMouseEnter={() => {
              if (isContactTab) {
                document.querySelector(`.contact-button`).style.backgroundColor = "#ff6666"; // Hover color
              }
            }}
            onMouseLeave={() => {
              if (isContactTab) {
                document.querySelector(`.contact-button`).style.backgroundColor =
                  activeTab === tab ? "#ff2224" : "#ff4447"; // Reset color
              }
            }}
          >
            {tabLabels[tab]}
          </button>
        );
      })}
    </motion.footer>
  );
};

export default Footer;
