import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion"; // Import AnimatePresence and motion
import "../../../styles/main.css";

const SlideContent = ({ slide }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [isInitialLoad, setIsInitialLoad] = useState(true);

  useEffect(() => {
    // Set content visibility to true when it is loaded
    setIsVisible(true);

    // Mark the initial load as false after a short delay
    const timer = setTimeout(() => setIsInitialLoad(false), 5000); // Adjust duration if needed

    return () => {
      clearTimeout(timer);
      setIsVisible(false);
    };
  }, [slide]);

  if (!slide || (!slide.image && !slide.text)) {
  }

  return (
    <AnimatePresence mode="wait">
      {slide.image && ( // Ensure the slide.image condition wraps the motion.img
        <motion.img
          key={rightActiveTab} // Use the image path as the key for AnimatePresence
          src={slide.image}
          className="slide-image"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }} // Define fade-out effect
          transition={{
            duration: isInitialLoad ? 2.5 : 1, // Keep existing durations
            delay: isInitialLoad ? 1 : 0, // Adjust delay only for initial load
          }}
        />
      )}
    </AnimatePresence>
  );
}

export default SlideContent;
